func:
msg.size = msg.payload.split(/\r\n|\r|\n/).length
if (msg.size == 2) {msg = null;}
return msg;

let myDate = new Date()
myDate.setDate(myDate.getDate()-1);
myDate = myDate.toISOString().split('T')[0]
msg.myDate = 'AssetIngestionReport-Prod-'+myDate +".csv"

msg.payload = 'rm '+msg.myDate
return msg;

msg.name = msg.req.files[0].originalname;

if (msg.req.files[0].mimetype.includes('image')) {
    msg.payload = `<img src="data:image/gif;base64,${msg.req.files[0].buffer.toString('base64')}">`;
} else {
    msg.payload = msg.req.files[0].buffer.toString();
}

return msg;

